// -----------------------------------------------------------------
// ARQUIVO: index.js
// -----------------------------------------------------------------

// --- 1. Importações ---
const express = require('express');
const cors = require('cors');

// --- 2. Inicialização do App ---
const app = express();

// --- 3. Configuração (Middlewares) ---

// Habilita o CORS (O "Porteiro")
// Configurado para permitir requisições APENAS do seu frontend no GitHub Pages.
const corsOptions = {
  origin: 'https://alvarohs84.github.io'
};
app.use(cors(corsOptions));

// Habilita o Express para entender JSON no corpo das requisições
// É assim que você consegue ler os dados do formulário (req.body)
app.use(express.json());

// --- 4. Rotas da API ---

/**
 * Rota de "saúde" (Health Check)
 * Boa prática para saber se a API está online.
 */
app.get('/', (req, res) => {
  res.send('API do FisioManager está online e pronta!');
});

/**
 * Rota para CADASTRAR um novo paciente
 * É AQUI QUE O ERRO 500 ACONTECE
 */
app.post('/pacientes', async (req, res) => {
  // O console.log aparecerá nos LOGS do RENDER.
  // Use isso para depurar.
  console.log('Recebida requisição POST em /pacientes');
  console.log('Dados recebidos (req.body):', req.body);

  try {
    // 1. Pega os dados do corpo da requisição
    // IMPORTANTE: Os nomes (nome, telefone, etc.) têm que ser
    // exatamente os mesmos que o seu frontend envia.
    const { nome, telefone, dataNascimento, sexo, diagnostico, avaliacao } = req.body;

    // 2. Validação simples (Exemplo)
    if (!nome) {
      // Se não tiver nome, retorna um erro 400 (Bad Request)
      console.log('Erro de validação: Nome é obrigatório.');
      return res.status(400).json({ error: 'O nome do paciente é obrigatório.' });
    }

    // ---------------------------------------------------------
    // ▼▼▼ AQUI VAI SUA LÓGICA DO BANCO DE DADOS ▼▼▼
    // ---------------------------------------------------------
    // Ex: Inserir os dados (nome, telefone, etc.) no seu banco de dados (PostgreSQL, MongoDB, etc.)
    //
    // const novoPaciente = await seuPoolDeDB.query(
    //   'INSERT INTO pacientes (nome, telefone, ...) VALUES ($1, $2, ...)',
    //   [nome, telefone, ...]
    // );
    //
    // Se esta lógica falhar, o 'catch' (abaixo) será executado.
    // ---------------------------------------------------------
    // ▲▲▲ FIM DA LÓGICA DO BANCO DE DADOS ▲▲▲
    // ---------------------------------------------------------

    // 3. Se tudo deu certo, envie uma resposta de sucesso (201 - Created)
    console.log(`Paciente "${nome}" salvo com sucesso.`);
    res.status(201).json({ message: 'Paciente salvo com sucesso!', data: req.body });

  } catch (error) {
    // 4. Se qualquer coisa no bloco 'try' falhar (o banco de dados, etc.)
    // este bloco 'catch' será executado.
    //
    // ESTE É O "ERRO 500 (Internal Server Error)"
    //
    // O console.error é a parte MAIS IMPORTANTE. Ele vai mostrar o erro
    // exato nos seus LOGS do RENDER.
    console.error('ERRO INTERNO NO SERVIDOR AO SALVAR PACIENTE:', error);

    // Envia uma resposta de erro genérica para o frontend
    res.status(500).json({ error: 'Ocorreu um erro interno no servidor. Tente novamente mais tarde.' });
  }
});

// --- 5. Inicialização do Servidor ---

// O Render.com define a porta através da variável de ambiente 'process.env.PORT'
const PORT = process.env.PORT || 3001; // Use a porta 3001 se estiver rodando local

app.listen(PORT, () => {
  console.log(`Servidor rodando na porta ${PORT}`);
});
